import { makeStyles } from "@material-ui/core/styles";
export default makeStyles((theme)=>({
appBar:{
    borderRadius:15,
    margin:'30 px 0',
    display:'flex',
    flexDirection:'row',
    justifyContent:'center',
    alignItems:'center',
},
heading:{
    color:'rgba(0,183,255,1)'
},
Container:{
    margin :20
    
  },
image:{
    marginLeft:'15px'
},
[theme.breakpoints.down('sm')]:{
mainContainer:{
    flexDirection:'column-reverse'
}
}
}))
